print(sum(ord((c := chr(ord(x[2]) - 23))) - 64 + (6 if {"A": "C", "B": "A", "C": "B"}[c] == x[0] else (3 if x[0] == c else 0)) for x in open("days/02/input.txt").readlines()), sum(({"X": 0, "Y": 3, "Z": 6}[x[2]] + ord({"A": "C", "B": "A", "C": "B"}[x[0]] if x[2] == "X" else (x[0] if x[2] == "Y" else {"A": "B", "B": "C", "C": "A"}[x[0]])) - 64)for x in open("days/02/input.txt").readlines()))
